    <script>
        $(document).ready(function() {

            $('.header__nav__mob__burger').click(function() {
                console.log(1);
                if ($('.header__nav__mob__burger').hasClass('header__nav__mob__burger-active')) {
                    $('.header__nav__mob__burger').removeClass('header__nav__mob__burger-active');

                    $('.header__nav__mob').removeClass('header__nav__mob-active')

                    $('.header__nav__mob__burger').children('.header__nav__mob__burger__item').eq(0).css({'left' : '0', 'top' : '0', 'transform' : 'rotate(0deg) translate3d(0%, 0, 0)'});
