                    $('.header__nav__mob__burger').children('.header__nav__mob__burger__item').eq(2).css({'opacity' : '1', 'width' : '100%'});
                }
                else {
                    $('.header__nav__mob__burger').addClass('header__nav__mob__burger-active');

                    $('.header__nav__mob').addClass('header__nav__mob-active')

                    $('.header__nav__mob__burger').children('.header__nav__mob__burger__item').eq(0).css({'top' : '50%', 'transform' : 'rotate(-45deg) translate3d(0, -50%, 0)'});
                    $('.header__nav__mob__burger').children('.header__nav__mob__burger__item').eq(1).css({'transform' : 'rotate(45deg) translate3d(0, -0.5px, 0)'});
                    $('.header__nav__mob__burger').children('.header__nav__mob__burger__item').eq(2).css({'opacity' : '0', 'width' : '0'});
                }

            });

            $('.header__user__name').click(function() {
                console.log(1);
                if ($('.header__user__menu').hasClass('header__user__menu-open')) {
                    $('.header__user__menu').removeClass('header__user__menu-open');
                    $('.header__user__name').children('.header__user__name__arrow').removeClass('header__user__name__arrow-open');
